INSERT INTO `book` VALUES (35, 'https://storage.googleapis.com/webproject-65086.appspot.com/Sach%20Giao%20Khoa/B%C3%A0i%20Lu%E1%BA%ADn%20M%E1%BA%ABu%20Ti%E1%BA%BFng%20Anh%20D%C3%A0nh%20Cho%20H%E1%BB%8Dc%20Sinh%20Thi%20THPT%20Chuy%C3%AAn%20H%C3%A0%20N%E1%BB%99i.jpeg', 'Bài Luận Mẫu Tiếng Anh Dành Cho Học Sinh Thi THPT Chuyên Hà Nội\r\n', 'Bài Luận Mẫu Tiếng Anh Dành Cho Học Sinh Thi THPT Chuyên Hà Nội\r\n\r\nNội dung sách đã cập nhật hết đề thi các trường chuyên Hà Nội tính tới năm học 2022-2023.\r\n\r\nSách dành cho các trường chuyên khu vực Hà Nội', 3, 190000, 'Mai Thành Sơn, Lê Hương Ly\r\n', 12, 'Tiếng Việt', 100, 2);
INSERT INTO `book` VALUES (36, 'https://storage.googleapis.com/webproject-65086.appspot.com/Sach%20Giao%20Khoa/B%C3%A9%20T%E1%BB%B1%20H%E1%BB%8Dc%20C%C3%B9ng%20Bubu%20-%20T%E1%BA%ADp%20Vi%E1%BA%BFt%20S%E1%BB%91%20(T%C3%A1i%20B%E1%BA%A3n).jpeg', 'Bé Tự Học Cùng Bubu - Tập Viết Số (Tái Bản)\r\n', 'Qua bộ sách Bé học lễ giáo các bé đã cùng bạn BuBu làm quen với các thói quen hàng ngày; cách  chăm sóc bản thân; các đức tính tốt,  xấu; cách vui chơi cùng bạn bè, cách cư xử với mọi người… Bạn BuBu đã gắn liền cùng các em ở rất hoạt động trong cuộc sống. Còn với bộ sách BÉ TỰ HỌC CÙNG BUBU, bạn BuBu sẽ đồng hành cùng các em trong việc học tập, giúp các em  làm quen với  số đếm, luyện tập các phép tính cộng, tính trừ và tập viết chữ cái, tập tô màu trước khi tới trường.\r\n\r\nBộ sách cũng là phương tiện giúp các bậc phụ huynh hướng dẫn các bé ở tuổi mẫu giáo và lớp 1  tự học tại nhà.\r\n\r\nTẬP VIẾT SỐ, ở tập sách này các bé sẽ cùng bạn BuBu tập viết số từ 1 đến 10 nhé!', 3, 25000, 'Biên Thùy, Hùng Tân\r\n', 3, 'Tiếng Việt', 100, 3);
INSERT INTO `book` VALUES (37, 'https://storage.googleapis.com/webproject-65086.appspot.com/Sach%20Giao%20Khoa/Combo%20S%C3%A1ch%20Chinh%20Ph%E1%BB%A5c%20Luy%E1%BB%87n%20Thi%20V%C3%A0o%20L%E1%BB%9Bp%2010%20Theo%20Ch%E1%BB%A7%20%C4%90%E1%BB%81%20-%20M%C3%B4n%20Ti%E1%BA%BFng%20Anh%20%2B%20M%C3%B4n%20Ng%E1%BB%AF%20V%C4%83n%20%2B%20M%C3%B4n%20To%C3%A1n%20(B%E1%BB%99%203%20Cu%E1%BB%91n).jpeg', 'Combo Sách Chinh Phục Luyện Thi Vào Lớp 10 Theo Chủ Đề - Môn Tiếng Anh + Môn Ngữ Văn + Môn Toán (Bộ 3 Cuốn)\r\n', 'Chinh phục luyện thi vào 10 môn Tiếng Anh theo chủ đề là cuốn sách trong bộ sách “Chinh phục luyện thi vào 10 theo chủ đề” được biên soạn bởi các tác giả uy tín và nhiều năm kinh nghiệm giảng dạy, luyện thi vào lớp 10. Cuốn sách được tổng hợp bài bản, cập nhật mới nhất các nội dung được đưa vào đề thi môn tiếng Anh những năm gần đây, chắc chắn sẽ giúp các em vững vàng kiến thức, tự tin đạt điểm cao trong kỳ thi sắp tới.', 3, 477000, 'Dương Hương, Đỗ Thị Thu Hà, Nguyễn Xuân Nam\r\n', 12, 'Tiếng Việt', 100, 3);
